#include "gtest/gtest.h"
#include "120Triangle.h"

TEST(case_1, content) {
    std::vector<std::vector<int>> triangle = {
        {1},
        {1,1}
    };

    int ans = 2;
    EXPECT_EQ(minimumTotal(triangle), ans);
    EXPECT_EQ(minimumTotalDp(triangle), ans);
    EXPECT_EQ(minimumTotal1(triangle), ans);;
    EXPECT_EQ(minimumTotalDp1(triangle), ans);
}

// triangle = {{2},{3,4},{6,5,7},{4,1,8,3}}
TEST(case_2, content) {
    std::vector<std::vector<int>> triangle = {
        {2},
        {3,4},
        {6,5,7},
        {4,1,8,3}
    };

    int ans = 11;
    EXPECT_EQ(minimumTotal(triangle), ans);
    EXPECT_EQ(minimumTotalDp(triangle), ans);
    EXPECT_EQ(minimumTotal1(triangle), ans);;
    EXPECT_EQ(minimumTotalDp1(triangle), ans);
}


TEST(case_3, content) {
    std::vector<std::vector<int>> triangle = {
        {-10},
    };

    int ans = -10;
    EXPECT_EQ(minimumTotal(triangle), ans);
    EXPECT_EQ(minimumTotalDp(triangle), ans);
    EXPECT_EQ(minimumTotal1(triangle), ans);;
    EXPECT_EQ(minimumTotalDp1(triangle), ans);
}

