#include "115.DistinctSubsequences.h"
#include "gtest/gtest.h"

TEST(case_1, content) {
    std::string s = "rabbbit", t = "rabbit";
    int ans = 3;
    EXPECT_EQ(numDistinct(s, t), ans);
}


